import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Alert,
  CircularProgress,
} from '@mui/material';
import axios from 'axios';

interface TrafficGeneratorProps {}

const TrafficGenerator: React.FC<TrafficGeneratorProps> = () => {
  const [formData, setFormData] = useState({
    interface: '',
    moatPrivateIp: '',
    privateIp: '',
    nodeIndex: '',
    totalDuration: '',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(null);

    try {
      const response = await axios.post('/api/traffic-generator/run', formData);
      setSuccess('Traffic generation started successfully!');
    } catch (err) {
      setError('Failed to start traffic generation. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ maxWidth: 600, mx: 'auto' }}>
      <Typography variant="h5" gutterBottom>
        Traffic Generator Configuration
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>
          {success}
        </Alert>
      )}

      <TextField
        fullWidth
        label="Network Interface"
        name="interface"
        value={formData.interface}
        onChange={handleInputChange}
        margin="normal"
        required
      />

      <TextField
        fullWidth
        label="MOAT Private IP"
        name="moatPrivateIp"
        value={formData.moatPrivateIp}
        onChange={handleInputChange}
        margin="normal"
        required
      />

      <TextField
        fullWidth
        label="Private IP"
        name="privateIp"
        value={formData.privateIp}
        onChange={handleInputChange}
        margin="normal"
        required
      />

      <TextField
        fullWidth
        label="Node Index"
        name="nodeIndex"
        type="number"
        value={formData.nodeIndex}
        onChange={handleInputChange}
        margin="normal"
        required
      />

      <TextField
        fullWidth
        label="Total Duration (seconds)"
        name="totalDuration"
        type="number"
        value={formData.totalDuration}
        onChange={handleInputChange}
        margin="normal"
        required
      />

      <Button
        type="submit"
        variant="contained"
        color="primary"
        fullWidth
        sx={{ mt: 3 }}
        disabled={loading}
      >
        {loading ? <CircularProgress size={24} /> : 'Start Traffic Generation'}
      </Button>
    </Box>
  );
};

export default TrafficGenerator;
