import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  AppBar,
  Box,
  Container,
  Paper,
  Tab,
  Tabs,
  Toolbar,
  Typography,
} from '@mui/material';
import TrafficGenerator from './components/TrafficGenerator';
import DataCollection from './components/DataCollection';
import logo from './assets/shugo-logo.png'; // Make sure to add your logo to src/assets/

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#03FFF6',   // Medium violet-blue (blend of blue and purple)
      light: '#8A6CFF',  // Lighter violet-blue with a cyan tint
      dark: '#3D1BB8',   // Darker purple-blue shade
    },
    secondary: {
      main: '#7871a0', // Light purple
      light: '#C4B5E0',
      dark: '#8A7BB5',
    },
    background: {
      default: '#1A1A2E', // Dark blue-purple
      paper: '#242444',   // Slightly lighter blue-purple
    },
    text: {
      primary: '#E0E0E0',
      secondary: '#B8B8B8',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h1: {
      fontWeight: 700,
    },
    h2: {
      fontWeight: 600,
    },
    h3: {
      fontWeight: 600,
    },
    h4: {
      fontWeight: 600,
    },
    h5: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          textTransform: 'none',
          fontWeight: 600,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
      },
    },
  },
});

interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

function App() {
  const [tabValue, setTabValue] = useState(0);

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        <AppBar position="static" elevation={0} sx={{ 
          backgroundColor: 'background.paper',
          borderBottom: '1px solid',
          borderColor: 'divider'
        }}>
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Î”ataprox by Shugo.io
            </Typography>
            <Box
              component="img"
              src={logo}
              alt="Shugo Logo"
              sx={{
                height: 40,
                width: 'auto',
                cursor: 'pointer',
                '&:hover': {
                  opacity: 0.8,
                },
              }}
              onClick={() => window.open('https://shugo.io', '_blank')}
            />
          </Toolbar>
        </AppBar>
        <Container component="main" sx={{ flexGrow: 1, py: 4 }}>
          <Paper 
            sx={{ 
              width: '100%',
              bgcolor: 'background.paper',
              color: 'text.primary'
            }}
          >
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
              <Tabs 
                value={tabValue} 
                onChange={handleTabChange} 
                aria-label="dashboard tabs"
                textColor="primary"
                indicatorColor="primary"
              >
                <Tab label="Traffic Generator" />
                <Tab label="Data Collection" />
              </Tabs>
            </Box>
            <TabPanel value={tabValue} index={0}>
              <TrafficGenerator />
            </TabPanel>
            <TabPanel value={tabValue} index={1}>
              <DataCollection />
            </TabPanel>
          </Paper>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
