import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Alert,
  CircularProgress,
  Grid,
  Divider,
  Stack,
  ThemeProvider,
  createTheme,
} from '@mui/material';
import axios from 'axios';

// Create a theme that matches dashprox.com
const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#00ff9d', // Bright green from dashprox
    },
    secondary: {
      main: '#ff00ff', // Pink/magenta from dashprox
    },
    background: {
      default: '#0a0a0a', // Dark background
      paper: '#1a1a1a', // Slightly lighter dark for cards
    },
    text: {
      primary: '#ffffff',
      secondary: '#b3b3b3',
    },
  },
  components: {
    MuiTextField: {
      styleOverrides: {
        root: {
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: '#333333',
            },
            '&:hover fieldset': {
              borderColor: '#00ff9d',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#00ff9d',
            },
          },
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: '4px',
          textTransform: 'none',
          fontWeight: 600,
        },
        contained: {
          '&:hover': {
            backgroundColor: '#00cc7d',
          },
        },
      },
    },
    MuiDivider: {
      styleOverrides: {
        root: {
          borderColor: '#333333',
        },
      },
    },
  },
});

interface TrafficGeneratorProps {}

interface ProcessDetails {
  stdout: string;
  stderr: string;
  processStatus: string;
  pid: string;
  processStatusAfter5s: string;
}

const TrafficGenerator: React.FC<TrafficGeneratorProps> = () => {
  const [formData, setFormData] = useState({
    // SSH Configuration
    sshHost: '',
    sshUsername: '',
    sshPassword: '',
    sshKeyPath: '',
    // Traffic Generator Configuration
    interface: '',
    moatPrivateIp: '',
    privateIp: '',
    nodeIndex: '',
    totalDuration: '',
  });
  const [loading, setLoading] = useState(false);
  const [stopping, setStopping] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const [connectionStatus, setConnectionStatus] = useState<string | null>(null);
  const [processDetails, setProcessDetails] = useState<ProcessDetails | null>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const testConnection = async () => {
    setLoading(true);
    setError(null);
    setConnectionStatus(null);

    try {
      const response = await axios.post('/api/traffic-generator/test-connection', {
        sshHost: formData.sshHost,
        sshUsername: formData.sshUsername,
        sshPassword: formData.sshPassword,
        sshKeyPath: formData.sshKeyPath,
      });
      setConnectionStatus('Successfully connected to the traffic generator machine!');
    } catch (err) {
      setError('Failed to connect to the traffic generator machine. Please check your SSH credentials.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(null);
    setProcessDetails(null);

    try {
      const response = await axios.post('/api/traffic-generator/run', formData);
      setSuccess('Traffic generation started successfully!');
      if (response.data.details) {
        setProcessDetails(response.data.details);
      }
    } catch (err: any) {
      setError(err.response?.data?.details || 'Failed to start traffic generation. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleStop = async () => {
    setStopping(true);
    setError(null);
    setSuccess(null);

    try {
      // First stop the traffic processes
      const stopResponse = await axios.post('/api/traffic-generator/stop', {
        sshHost: formData.sshHost,
        sshUsername: formData.sshUsername,
        sshPassword: formData.sshPassword,
        sshKeyPath: formData.sshKeyPath,
      });

      // Then cleanup the tail process if we have a PID
      if (processDetails?.pid) {
        await axios.post('/api/traffic-generator/cleanup', {
          pid: processDetails.pid,
          sshHost: formData.sshHost,
          sshUsername: formData.sshUsername,
          sshPassword: formData.sshPassword,
          sshKeyPath: formData.sshKeyPath,
        });
      }

      setSuccess('Traffic processes stopped successfully!');
      setProcessDetails(null);
    } catch (err) {
      setError('Failed to stop traffic processes. Please try again.');
      console.error('Error:', err);
    } finally {
      setStopping(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Box 
        component="form" 
        onSubmit={handleSubmit} 
        sx={{ 
          maxWidth: 800, 
          mx: 'auto',
          p: 3,
          bgcolor: 'background.default',
          minHeight: '100vh',
          color: 'text.primary'
        }}
      >
        <Typography 
          variant="h5" 
          gutterBottom 
          sx={{ 
            color: 'primary.main',
            fontWeight: 'bold',
            mb: 4
          }}
        >
          Traffic Generator Configuration
        </Typography>

        {error && (
          <Alert 
            severity="error" 
            sx={{ 
              mb: 2,
              bgcolor: 'rgba(211, 47, 47, 0.1)',
              '& .MuiAlert-icon': {
                color: '#ff6b6b'
              }
            }}
          >
            {error}
          </Alert>
        )}

        {success && (
          <Alert 
            severity="success" 
            sx={{ 
              mb: 2,
              bgcolor: 'rgba(46, 125, 50, 0.1)',
              '& .MuiAlert-icon': {
                color: '#00ff9d'
              }
            }}
          >
            {success}
          </Alert>
        )}

        {connectionStatus && (
          <Alert 
            severity="success" 
            sx={{ 
              mb: 2,
              bgcolor: 'rgba(46, 125, 50, 0.1)',
              '& .MuiAlert-icon': {
                color: '#00ff9d'
              }
            }}
          >
            {connectionStatus}
          </Alert>
        )}

        {processDetails && (
          <Box 
            sx={{ 
              mb: 2, 
              p: 2, 
              bgcolor: 'background.paper', 
              borderRadius: 1,
              border: '1px solid #333333'
            }}
          >
            <Typography 
              variant="h6" 
              gutterBottom
              sx={{ color: 'primary.main' }}
            >
              Process Details
            </Typography>
            {processDetails.stdout && (
              <Box sx={{ mb: 2 }}>
                <Typography variant="subtitle2" color="text.secondary">
                  Output:
                </Typography>
                <pre style={{ 
                  margin: 0, 
                  whiteSpace: 'pre-wrap',
                  color: '#00ff9d',
                  backgroundColor: 'rgba(0, 255, 157, 0.05)',
                  padding: '8px',
                  borderRadius: '4px'
                }}>
                  {processDetails.stdout}
                </pre>
              </Box>
            )}
            {processDetails.stderr && (
              <Box sx={{ mb: 2 }}>
                <Typography variant="subtitle2" color="text.secondary">
                  Errors:
                </Typography>
                <pre style={{ 
                  margin: 0, 
                  whiteSpace: 'pre-wrap', 
                  color: '#ff6b6b',
                  backgroundColor: 'rgba(255, 107, 107, 0.05)',
                  padding: '8px',
                  borderRadius: '4px'
                }}>
                  {processDetails.stderr}
                </pre>
              </Box>
            )}
            {processDetails.processStatus && (
              <Box>
                <Typography variant="subtitle2" color="text.secondary">
                  Process Status:
                </Typography>
                <pre style={{ 
                  margin: 0, 
                  whiteSpace: 'pre-wrap',
                  color: '#00ff9d',
                  backgroundColor: 'rgba(0, 255, 157, 0.05)',
                  padding: '8px',
                  borderRadius: '4px'
                }}>
                  {processDetails.processStatus}
                </pre>
              </Box>
            )}
          </Box>
        )}

        <Typography 
          variant="h6" 
          sx={{ 
            mt: 3, 
            mb: 2,
            color: 'primary.main'
          }}
        >
          SSH Connection
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="SSH Host"
              name="sshHost"
              value={formData.sshHost}
              onChange={handleInputChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="SSH Username"
              name="sshUsername"
              value={formData.sshUsername}
              onChange={handleInputChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="SSH Password"
              name="sshPassword"
              type="password"
              value={formData.sshPassword}
              onChange={handleInputChange}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="SSH Key Path (optional)"
              name="sshKeyPath"
              value={formData.sshKeyPath}
              onChange={handleInputChange}
              helperText="Path to private key file if using key-based authentication"
            />
          </Grid>
        </Grid>

        <Button
          variant="outlined"
          color="primary"
          onClick={testConnection}
          disabled={loading || stopping}
          sx={{ 
            mt: 2,
            borderColor: 'primary.main',
            '&:hover': {
              borderColor: 'primary.main',
              bgcolor: 'rgba(0, 255, 157, 0.1)'
            }
          }}
        >
          Test Connection
        </Button>

        <Divider sx={{ my: 4 }} />

        <Typography 
          variant="h6" 
          sx={{ 
            mb: 2,
            color: 'primary.main'
          }}
        >
          Traffic Generator Settings
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Network Interface"
              name="interface"
              value={formData.interface}
              onChange={handleInputChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="MOAT Private IP"
              name="moatPrivateIp"
              value={formData.moatPrivateIp}
              onChange={handleInputChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Private IP"
              name="privateIp"
              value={formData.privateIp}
              onChange={handleInputChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Node Index"
              name="nodeIndex"
              type="number"
              value={formData.nodeIndex}
              onChange={handleInputChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Total Duration (seconds)"
              name="totalDuration"
              type="number"
              value={formData.totalDuration}
              onChange={handleInputChange}
              required
            />
          </Grid>
        </Grid>

        <Stack direction="row" spacing={2} sx={{ mt: 3 }}>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
            disabled={loading || stopping}
            sx={{
              bgcolor: 'primary.main',
              '&:hover': {
                bgcolor: '#00cc7d'
              }
            }}
          >
            {loading ? <CircularProgress size={24} color="inherit" /> : 'Start Traffic Generation'}
          </Button>
          <Button
            variant="contained"
            color="error"
            fullWidth
            onClick={handleStop}
            disabled={loading || stopping}
            sx={{
              bgcolor: '#ff6b6b',
              '&:hover': {
                bgcolor: '#ff5252'
              }
            }}
          >
            {stopping ? <CircularProgress size={24} color="inherit" /> : 'Stop Traffic'}
          </Button>
        </Stack>
      </Box>
    </ThemeProvider>
  );
};

export default TrafficGenerator;
